import React from 'react'
import api from '../api'
import Navbar from '../../components/Navbar'
import {
  LineChart,
  XAxis,
  CartesianGrid,
  Line,
  Tooltip,
  YAxis,
  Label
} from 'recharts'  

const Markets = () => {
    // Create state variables
    let [responseData, setResponseData] = React.useState('')
    let [ticker, setTicker] = React.useState('')
    let [message, setMessage] = React.useState('')
    // fetches stock data based on parameters
    const fetchData = (e) => {
        e.preventDefault()
        setMessage('Loading...')
        api.stockTimeSeries(ticker)
        .then((response)=>{
           setResponseData(response.data)
           setMessage('')
           console.log(response)
        })
        .catch((error) => {
           setMessage('Error')
           console.log(error)
        })
    }
    return (
        <div
            style={{
                background: '#EEE',
                padding: '10%',
            }}
            >
            <Navbar/>
            <h1
                style={{
                    background: 'black',
                    color: 'white',
                    padding: '1rem',
                    display: 'inline-block'
                }}>Stock Market</h1>
            <h2>Analyze Stock Data</h2>
            <form onSubmit={fetchData}>
                <fieldset style={{padding:4}}>
                    <legend>Search Stock Market</legend>
                    <label style = {{ 
                        paddingLeft: 10,
                        // paddingRight: 450,
                        }}htmlFor="ticker">
                Enter Here:    
                        <input
                            style={{
                                marginInline: 10,
                                inlineSize: 624
                            }}
                            required
                            name="ticker"
                            id="ticker"
                            type='text'
                            placeholder='Insert stock ticker such as AAPL, TSLA etc.'
                            value={ticker}
                            onChange={(e) => setTicker(e.target.value)}
                        />
                    </label>
                    <button type='submit'
                    style={{
                        borderRadius: 5,
                        marginBottom: 10,
                        cursor: PointerEvent,
                        maxWidth: 150,
                        background: '#0e6d8a'}}>Submit</button>
                </fieldset>
            </form>
            <p>{message}</p>
            <h3>Symbol: {responseData ? responseData.symbol : ''}</h3>
            <p>Daily Time Series with Splits and Dividend Events</p>
            <small>Last Refresh: {responseData ? responseData.refreshed : ''}</small>
            <LineChart
                width={900}
                height={400}
                data={responseData.closePrices}
                margin={{ top: 50, right: 20, left: 10, bottom: 5 }}
                >
                <YAxis tickCount={10} type="number" width={80}>
                    <Label value="Close Price" position="insideLeft" angle={270} />
                </YAxis>
                <Tooltip />
                <XAxis padding={{left: 5, right: 5}} tickCount={10} angle={-60} height={90} dataKey="date" />
                <CartesianGrid stroke="#f5f5f5" />
                <Line type="monotone" dataKey="close" stroke="#ff7300" yAxisId={0} />
            </LineChart>

            <button type='buysell'
                    style={{
                        borderRadius: 5,
                        marginBottom: 10,
                        justifyContent: 'center',
                        alignItems: 'center',
                        position: 'relative',
                        cursor: PointerEvent,
                        maxWidth: 150,
                        background: '#d4281c'}}>Buy / Sell Stock</button>
        </div>
    )
}

export default Markets
